  final bool local;
  final List<GraphicalResource> sources;

  bool get isAdaptive => sources.length != 1;

  String? get firstFileName => sources[0].path;
  String? get secondFileName => isAdaptive?sources[1].path:null;

  String? get themedFileName{
    if(secondFileName == null) return firstFileName;
    return AppSettings.isDark?secondFileName:firstFileName;
  }

  String? get firstAuthor => sources[0].author;
  String? get secondAuthor => isAdaptive?sources[1].author:null;

  String? get code{
    if(!local) return firstFileName;

    if(isAdaptive)
      return '${CommunityCoverImageData.localPrefix}${firstFileName!.split('#')[0]}#';

    return CommunityCoverImageData.localPrefix + firstFileName!.split('.')[0];
  }

  const CommunityCoverImageData(this.local, this.sources);

  static CommunityCoverImageData from(String imgUrlOrCode){

    if(!imgUrlOrCode.startsWith(localPrefix))
      return CommunityCoverImageData(false, [GraphicalResource(imgUrlOrCode, null)]);

    imgUrlOrCode = imgUrlOrCode.replaceAll(localPrefix, '');
    String author = 'freepik.com (${imgUrlOrCode.split(':')[0]})';

    if(!imgUrlOrCode.endsWith('#'))
      return CommunityCoverImageData(true, [GraphicalResource('$imgUrlOrCode.webp', author)]);

    return CommunityCoverImageData(true, [
      GraphicalResource('${imgUrlOrCode}light.webp', author),
      GraphicalResource('${imgUrlOrCode}night.webp', author)
    ]);

  }

